import React, { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';

interface Listing {
  id: string;
  address: string;
  city: string;
  state: string;
  zip_code?: string;
  price?: number;
  bedrooms?: number;
  bathrooms?: number;
  square_feet?: number;
  listing_date?: string;
  status?: string;
  [key: string]: any;
}

interface SearchPanelProps {
  address: string;
  onAddressChange: (address: string) => void;
  onFetchPhotos: () => void;
  onClear: () => void;
  recentSearches: string[];
  onListingSelect?: (listing: Listing) => void;
}

export default function SearchPanel({
  address,
  onAddressChange,
  onFetchPhotos,
  onClear,
  recentSearches,
  onListingSelect
}: SearchPanelProps) {
  const [isMapExpanded, setIsMapExpanded] = useState(false);
  const [suggestions, setSuggestions] = useState<Listing[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [loading, setLoading] = useState(false);

  // Search for listings as user types
  useEffect(() => {
    const searchListings = async () => {
      if (address.length < 1) {
        setSuggestions([]);
        setShowSuggestions(false);
        return;
      }

      setLoading(true);
      console.log('Searching for:', address);

      try {
        // Search both current and sold listings
        const [currentListings, soldListings] = await Promise.all([
          supabase
            .from('just_listed')
            .select('*')
            .or(`address.ilike.%${address}%, addresscity.ilike.%${address}%, addressstate.ilike.%${address}%`)
            .limit(10),
          supabase
            .from('sold_listings')
            .select('*')
            .or(`address.ilike.%${address}%, addresscity.ilike.%${address}%, addressstate.ilike.%${address}%`)
            .limit(10)
        ]);

        console.log('Current listings:', currentListings);
        console.log('Sold listings:', soldListings);

        const allSuggestions = [
          ...(currentListings.data || []),
          ...(soldListings.data || [])
        ];

        console.log('All suggestions:', allSuggestions);
        setSuggestions(allSuggestions);
        setShowSuggestions(true);
      } catch (error) {
        console.error('Error searching listings:', error);
        setSuggestions([]);
      } finally {
        setLoading(false);
      }
    };

    const timeoutId = setTimeout(searchListings, 200); // Reduced debounce for faster response
    return () => clearTimeout(timeoutId);
  }, [address]);

  const handleSuggestionClick = (listing: Listing) => {
    const fullAddress = `${listing.address}, ${listing.addresscity}, ${listing.addressstate}`;
    onAddressChange(fullAddress);
    setShowSuggestions(false);

    // Store the selected listing for photo fetching
    if (onListingSelect) {
      onListingSelect(listing);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onAddressChange(e.target.value);
  };

  const handleInputFocus = () => {
    if (suggestions.length > 0) {
      setShowSuggestions(true);
    } else if (address.length > 0) {
      // Trigger search if there's text but no suggestions yet
      setShowSuggestions(true);
    }
  };

  const handleInputBlur = () => {
    // Delay hiding suggestions to allow clicks
    setTimeout(() => setShowSuggestions(false), 200);
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      <h2 className="text-lg font-semibold text-gray-900 mb-4">Search</h2>

      {/* Address Input with Autocomplete */}
      <div className="mb-4 relative">
        <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-2">
          Address
        </label>
        <div className="relative">
          <input
            id="address"
            type="text"
            value={address}
            onChange={handleInputChange}
            onFocus={handleInputFocus}
            onBlur={handleInputBlur}
            placeholder="Enter address for autocomplete..."
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
          {loading && (
            <div className="absolute right-3 top-2.5">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
            </div>
          )}
        </div>

        {/* Autocomplete Suggestions */}
        {showSuggestions && suggestions.length > 0 && (
          <div className="absolute z-50 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-y-auto">
            {suggestions.map((listing) => (
              <div
                key={listing.id}
                onClick={() => handleSuggestionClick(listing)}
                className="px-3 py-2 hover:bg-blue-50 cursor-pointer border-b border-gray-100 last:border-b-0 transition-colors"
              >
                <div className="font-medium text-gray-900">
                  {listing.address}
                </div>
                <div className="text-sm text-gray-600">
                  {listing.addresscity}, {listing.addressstate}
                  {listing.unformattedprice && (
                    <span className="ml-2 text-green-600 font-medium">
                      ${listing.unformattedprice.toLocaleString()}
                    </span>
                  )}
                </div>
                {listing.beds && listing.baths && (
                  <div className="text-xs text-gray-500">
                    {listing.beds} bed • {listing.baths} bath
                    {listing.area && ` • ${listing.area} sq ft`}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}

        {/* Loading indicator */}
        {showSuggestions && loading && (
          <div className="absolute z-50 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg p-3">
            <div className="flex items-center justify-center">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
              <span className="text-sm text-gray-500">Searching...</span>
            </div>
          </div>
        )}
      </div>

      {/* Action Buttons */}
      <div className="flex gap-2 mb-4">
        <button
          onClick={onFetchPhotos}
          className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors"
        >
          <div className="flex items-center justify-center">
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            Load Photos & Auto-Detect
          </div>
        </button>
        <button
          onClick={onClear}
          className="flex-1 bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 transition-colors"
        >
          Clear
        </button>
      </div>

      {/* Map Placeholder */}
      <div className="mb-4">
        <div className="border border-gray-300 rounded-md p-4 bg-gray-50">
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-sm font-medium text-gray-700">Map</h3>
            <button
              onClick={() => setIsMapExpanded(!isMapExpanded)}
              className="text-xs text-blue-600 hover:text-blue-800"
            >
              {isMapExpanded ? 'Collapse' : 'Expand'}
            </button>
          </div>

          {isMapExpanded && (
            <div className="space-y-3">
              <div>
                <label className="block text-xs text-gray-600 mb-1">From</label>
                <input
                  type="text"
                  value={address || "Current location"}
                  readOnly
                  className="w-full px-2 py-1 text-xs border border-gray-300 rounded bg-gray-100"
                />
              </div>
              <div>
                <label className="block text-xs text-gray-600 mb-1">To</label>
                <input
                  type="text"
                  placeholder="Destination address..."
                  className="w-full px-2 py-1 text-xs border border-gray-300 rounded focus:outline-none focus:ring-1 focus:ring-blue-500"
                />
              </div>
            </div>
          )}

          <div className="mt-2 h-32 bg-gray-200 rounded flex items-center justify-center">
            <span className="text-gray-500 text-sm">Map placeholder</span>
          </div>
        </div>
      </div>

      {/* Recent Searches */}
      <div>
        <h3 className="text-sm font-medium text-gray-700 mb-2">Recent Searches</h3>
        {recentSearches.length > 0 ? (
          <ul className="space-y-1">
            {recentSearches.map((search, index) => (
              <li key={index}>
                <button
                  onClick={() => onAddressChange(search)}
                  className="text-sm text-blue-600 hover:text-blue-800 hover:underline text-left w-full"
                >
                  {search}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-sm text-gray-500">No recent searches</p>
        )}
      </div>
    </div>
  );
}
