import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Detection, MappingTable, Estimate } from '../types';
import { calculateEstimate } from '../lib/estimate';
import { GoogleMapsService } from '../lib/googleMapsService';
import { SimplePDFService } from '../lib/simplePDFService';
import { SaturnGPTService, SaturnMoveTimeResponse } from '../lib/saturnGPTService';

interface LocationState {
  address: string;
  detections: Detection[];
  estimate: Estimate;
  mapping: MappingTable;
}

interface Upsell {
  id: string;
  name: string;
  description: string;
  price: number;
  recommended: boolean;
  selected: boolean;
}

export default function EstimatePage() {
  const navigate = useNavigate();
  const location = useLocation();
  const state = location.state as LocationState;

  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    phone: '',
    moveDate: ''
  });

  const [originAddress, setOriginAddress] = useState(state?.address || '');
  const [destinationAddress, setDestinationAddress] = useState('');
  const [detections] = useState<Detection[]>(state?.detections || []);
  const [mapping] = useState<MappingTable>(state?.mapping || {});
  const [baseEstimate] = useState<Estimate>(state?.estimate || {
    crew: 3,
    rate: 75,
    travelMins: 30,
    stairs: false,
    elevator: false,
    wrapping: false,
    safetyPct: 10,
    hours: 0,
    total: 0
  });

  // Generate upsells based on detected items - auto-select items that need special care
  const [upsells, setUpsells] = useState<Upsell[]>(() => {
    const initialUpsells: Upsell[] = [
      {
        id: 'insurance',
        name: 'Moving Insurance',
        description: 'Protect your belongings during the move',
        price: (state?.estimate?.total || 0) * 0.05, // 5% of total
        recommended: true,
        selected: false
      },
      {
        id: 'packing',
        name: 'Packing Service',
        description: 'Professional packing of your items',
        price: detections.reduce((sum, d) => sum + (d.qty * 25), 0), // $25 per item
        recommended: detections.length > 10,
        selected: false
      },
      {
        id: 'unpacking',
        name: 'Unpacking Service',
        description: 'We unpack and organize at your new location',
        price: detections.reduce((sum, d) => sum + (d.qty * 15), 0), // $15 per item
        recommended: false,
        selected: false
      }
    ];

    // Detect TVs - AUTO-SELECT TV boxes (they NEED special boxes)
    const tvDetections = detections.filter(d => 
      d.label.toLowerCase().includes('tv') || 
      d.label.toLowerCase().includes('television') ||
      d.label.toLowerCase().includes('flat screen')
    );
    const tvCount = tvDetections.reduce((sum, d) => sum + d.qty, 0);

    if (tvCount > 0) {
      initialUpsells.push({
        id: 'tv-boxes',
        name: `TV Boxes (${tvCount} TV${tvCount > 1 ? 's' : ''})`,
        description: 'Specialty boxes and protective wrapping - Required for safe transport',
        price: tvCount * 35,
        recommended: true,
        selected: true // AUTO-SELECTED - TVs need boxes!
      });
    }

    // Detect fragile items (arts, pictures, mirrors) - AUTO-SELECT fragile packing
    const fragileItems = detections.filter(d => {
      const label = d.label.toLowerCase();
      return label.includes('mirror') ||
        label.includes('glass') ||
        label.includes('art') ||
        label.includes('artwork') ||
        label.includes('picture') ||
        label.includes('painting') ||
        label.includes('frame') ||
        label.includes('lamp') ||
        label.includes('vase') ||
        label.includes('chandelier') ||
        label.includes('crystal');
    });

    if (fragileItems.length > 0) {
      const fragileQty = fragileItems.reduce((sum, d) => sum + d.qty, 0);
      initialUpsells.push({
        id: 'fragile-packing',
        name: 'Fragile Item Packing',
        description: `Specialized bubble wrap & packing for ${fragileQty} delicate item${fragileQty > 1 ? 's' : ''}`,
        price: fragileItems.reduce((sum, d) => sum + (d.qty * 20), 0),
        recommended: true,
        selected: true // AUTO-SELECTED - fragile items need protection!
      });
    }

    // Detect specialty/heavy items that need special handling - AUTO-SELECT special handling
    const specialtyItems = detections.filter(d => {
      const label = d.label.toLowerCase();
      return label.includes('piano') ||
        label.includes('grand piano') ||
        label.includes('upright piano') ||
        label.includes('treadmill') ||
        label.includes('exercise equipment') ||
        label.includes('pool table') ||
        label.includes('billiard') ||
        label.includes('safe') ||
        label.includes('gun safe') ||
        label.includes('aquarium') ||
        label.includes('fish tank') ||
        label.includes('grandfather clock') ||
        label.includes('antique') ||
        label.includes('statue');
    });

    if (specialtyItems.length > 0) {
      const specialtyQty = specialtyItems.reduce((sum, d) => sum + d.qty, 0);
      initialUpsells.push({
        id: 'specialty-handling',
        name: 'Specialty Item Handling',
        description: `Professional disassembly, wrapping & handling for ${specialtyQty} specialty item${specialtyQty > 1 ? 's' : ''} (piano, treadmill, etc.)`,
        price: specialtyItems.reduce((sum, d) => {
          // Higher price for complex items
          const isPiano = d.label.toLowerCase().includes('piano');
          const isTreadmill = d.label.toLowerCase().includes('treadmill');
          const isPoolTable = d.label.toLowerCase().includes('pool') || d.label.toLowerCase().includes('billiard');

          if (isPiano) return sum + (d.qty * 150); // Pianos are complex
          if (isTreadmill) return sum + (d.qty * 75);
          if (isPoolTable) return sum + (d.qty * 100);
          return sum + (d.qty * 60); // Other specialty items
        }, 0),
        recommended: true,
        selected: true // AUTO-SELECTED - these items need special handling!
      });
    }

    // Detect large appliances that might need special care
    const largeAppliances = detections.filter(d => {
      const label = d.label.toLowerCase();
      return label.includes('refrigerator') ||
        label.includes('fridge') ||
        label.includes('freezer') ||
        label.includes('wine cooler') ||
        label.includes('dishwasher');
    });

    if (largeAppliances.length > 0) {
      const applianceQty = largeAppliances.reduce((sum, d) => sum + d.qty, 0);
      initialUpsells.push({
        id: 'appliance-protection',
        name: 'Appliance Protection',
        description: `Protective wrapping for ${applianceQty} large appliance${applianceQty > 1 ? 's' : ''}`,
        price: largeAppliances.reduce((sum, d) => sum + (d.qty * 30), 0),
        recommended: largeAppliances.length > 1,
        selected: largeAppliances.length > 1 // Auto-select if multiple appliances
      });
    }

    return initialUpsells;
  });

  const [travelDistance, setTravelDistance] = useState(0);
  const [travelTime, setTravelTime] = useState(0);
  const [isCalculatingDistance, setIsCalculatingDistance] = useState(false);
  const [isCalculatingGPT, setIsCalculatingGPT] = useState(false);
  const [distanceError, setDistanceError] = useState<string | null>(null);
  const [gptError, setGptError] = useState<string | null>(null);
  const [finalEstimate, setFinalEstimate] = useState<Estimate>({ ...baseEstimate });
  const [gptReasoning, setGptReasoning] = useState<string | null>(null);
  const [gptBreakdown, setGptBreakdown] = useState<SaturnMoveTimeResponse['breakdown'] | null>(null);

  // Building/Parking details for GPT calculation
  const [originType, setOriginType] = useState<'house' | 'apartment' | 'condo' | 'business'>('house');
  const [destinationType, setDestinationType] = useState<'house' | 'apartment' | 'condo' | 'business'>('house');
  const [stairsDestination, setStairsDestination] = useState(false);
  const [elevatorDestination, setElevatorDestination] = useState(false);
  const [floorOrigin, setFloorOrigin] = useState<number | undefined>();
  const [floorDestination, setFloorDestination] = useState<number | undefined>();
  const [parkingOrigin, setParkingOrigin] = useState<'driveway' | 'street' | 'parking_lot' | 'difficult'>('driveway');
  const [parkingDestination, setParkingDestination] = useState<'driveway' | 'street' | 'parking_lot' | 'difficult'>('driveway');

  // Auto-calculate distance and GPT estimate when both addresses are provided
  useEffect(() => {
    const calculateGPTEstimate = async () => {
      if (!originAddress || !destinationAddress || destinationAddress.length < 10) {
        return; // Wait for complete addresses
      }

      if (detections.length === 0) {
        return; // Need inventory first
      }

      setIsCalculatingDistance(true);
      setIsCalculatingGPT(true);
      setDistanceError(null);
      setGptError(null);

      try {
        // Step 1: Get distance first
        const distanceResult = await GoogleMapsService.getDistance(
          originAddress,
          destinationAddress
        );

        setTravelDistance(Math.round(distanceResult.distance));
        setTravelTime(distanceResult.duration);

        // Step 2: Calculate total cubic feet and weight from detections
        const totalCubicFeet = detections.reduce((sum, d) => {
          // Priority: detected value > mapping > 0 (shouldn't happen with estimator)
          const cf = d.cubicFeet || mapping[d.label]?.cf || 0;
          return sum + (cf * d.qty);
        }, 0);

        const totalWeight = detections.reduce((sum, d) => {
          // Priority: detected weight > calculated from cubicFeet > mapping table > 0
          let weight = 0;
          if (d.weight && d.weight > 0) {
            weight = d.weight;
          } else if (d.cubicFeet && d.cubicFeet > 0) {
            weight = d.cubicFeet * 7;
          } else if (mapping[d.label]?.cf) {
            weight = mapping[d.label].cf * 7;
          }
          return sum + (weight * d.qty);
        }, 0);

        console.log(`ðŸ“Š Volume Calculation: ${totalCubicFeet.toFixed(1)} cu ft, ${totalWeight.toFixed(0)} lbs total from ${detections.length} items`);

        // Step 3: Call GPT for accurate move time calculation
        const gptResult = await SaturnGPTService.calculateMoveTime({
          detections: detections.map(d => ({
            label: d.label,
            qty: d.qty,
            cubicFeet: d.cubicFeet || mapping[d.label]?.cf,
            weight: d.weight || (mapping[d.label]?.cf ? mapping[d.label].cf * 7 : undefined),
            size: d.size,
            room: d.room
          })),
          distance: distanceResult.distance,
          travelTime: distanceResult.duration,
          originType,
          destinationType,
          stairsOrigin: baseEstimate.stairs,
          stairsDestination,
          elevatorOrigin: baseEstimate.elevator,
          elevatorDestination,
          floorOrigin,
          floorDestination,
          parkingOrigin,
          parkingDestination
        });

        // Step 4: Update estimate with GPT results
        setFinalEstimate({
          ...baseEstimate,
          hours: gptResult.hoursStandard, // Use standard estimate
          crew: gptResult.recommendedCrew,
          rate: gptResult.crewRate / gptResult.recommendedCrew, // Per mover rate
          total: gptResult.totalBeforeTax - gptResult.surchargesTotal, // Base total without specialty surcharges (they'll be upsells)
          travelMins: distanceResult.duration
        });

        // Step 5: Auto-apply detected upsells from GPT
        const newUpsells = [...upsells];

        // Update existing upsells with GPT recommendations
        gptResult.detectedUpsells.forEach(gptUpsell => {
          const existingIndex = newUpsells.findIndex(u => u.id === gptUpsell.id);
          if (existingIndex >= 0) {
            newUpsells[existingIndex] = {
              ...newUpsells[existingIndex],
              price: gptUpsell.price,
              description: gptUpsell.reason,
              recommended: true,
              selected: gptUpsell.required || newUpsells[existingIndex].selected
            };
          } else {
            // Add new upsell from GPT
            newUpsells.push({
              id: gptUpsell.id,
              name: gptUpsell.name,
              description: gptUpsell.reason,
              price: gptUpsell.price,
              recommended: true,
              selected: gptUpsell.required
            });
          }
        });

        // Add specialty item surcharges as separate upsells
        if (gptResult.specialtyItems.length > 0) {
          gptResult.specialtyItems.forEach(item => {
            const existingSpecialtyIndex = newUpsells.findIndex(u => u.id === `specialty-${item.category}`);
            if (existingSpecialtyIndex < 0) {
              newUpsells.push({
                id: `specialty-${item.category}`,
                name: `${item.item} Specialty Handling`,
                description: `Specialty handling for ${item.item} (+${item.extraTime} min)`,
                price: item.surcharge,
                recommended: true,
                selected: true // Specialty items are required
              });
            }
          });
        }

        setUpsells(newUpsells);
        setGptReasoning(gptResult.reasoning);
        setGptBreakdown(gptResult.breakdown);

      } catch (error: any) {
        console.error('GPT Estimate Error:', error);
        setGptError(error.message || 'Could not calculate estimate with GPT');

        // Fallback to simple calculation
        try {
          const distanceResult = await GoogleMapsService.getDistance(
            originAddress,
            destinationAddress
          );
          const updatedEstimate = {
            ...baseEstimate,
            travelMins: distanceResult.duration
          };
          const result = calculateEstimate(detections, mapping, updatedEstimate);
          setFinalEstimate({ ...updatedEstimate, ...result });
        } catch (fallbackError: any) {
          setDistanceError(fallbackError.message || 'Could not calculate distance');
        }
      } finally {
        setIsCalculatingDistance(false);
        setIsCalculatingGPT(false);
      }
    };

    // Debounce to avoid too many API calls
    const timer = setTimeout(() => {
      calculateGPTEstimate();
    }, 1500);

    return () => clearTimeout(timer);
  }, [originAddress, destinationAddress, detections, mapping, baseEstimate, originType, destinationType, stairsDestination, elevatorDestination, floorOrigin, floorDestination, parkingOrigin, parkingDestination]);

  // Calculate final total with upsells
  const totalUpsells = upsells.filter(u => u.selected).reduce((sum, u) => sum + u.price, 0);
  const finalTotal = finalEstimate.total + totalUpsells;

  const handleUpsellToggle = (id: string) => {
    setUpsells(prev => 
      prev.map(u => u.id === id ? { ...u, selected: !u.selected } : u)
    );
  };

  const handleContinue = () => {
    // Navigate to final quote/confirmation page or save quote
    navigate('/dashboard');
  };

  const handleSendQuote = () => {
    if (!customerInfo.name || !customerInfo.email || !customerInfo.phone || !customerInfo.moveDate) {
      alert('Please fill in all customer information fields');
      return;
    }

    if (!destinationAddress) {
      alert('Please enter destination address');
      return;
    }

    // Navigate to quote preview page
    navigate('/quote-preview', {
      state: {
        customerName: customerInfo.name,
        customerEmail: customerInfo.email,
        customerPhone: customerInfo.phone,
        moveDate: customerInfo.moveDate,
        originAddress,
        destinationAddress,
        detections,
        estimate: finalEstimate,
        upsells: upsells.filter(u => u.selected),
        totalAmount: finalTotal,
        photos: []
      }
    });
  };

  const handleDownloadPDF = async () => {
    try {
      const quoteData = {
        id: 'temp-' + Date.now(),
        customerName: customerInfo.name,
        customerEmail: customerInfo.email,
        customerPhone: customerInfo.phone,
        moveDate: customerInfo.moveDate,
        originAddress,
        destinationAddress,
        detections,
        estimate: finalEstimate,
        upsells: upsells.filter(u => u.selected),
        totalAmount: finalTotal,
        photos: []
      };

      await SimplePDFService.downloadQuote(quoteData);
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Failed to generate PDF. Please try again.');
    }
  };

  // Group detections by room
  const groupedDetections = detections.reduce((groups, detection) => {
    const room = detection.room || 'Unknown Room';
    if (!groups[room]) {
      groups[room] = [];
    }
    groups[room].push(detection);
    return groups;
  }, {} as Record<string, Detection[]>);

  const totalItems = detections.reduce((sum, d) => sum + d.qty, 0);
  const totalCubicFeet = detections.reduce((sum, detection) => {
    // Priority: 1) detected cubicFeet, 2) mapping table, 3) fallback to 0 (shouldn't happen with estimator)
    const cf = detection.cubicFeet || mapping[detection.label]?.cf || 0;
    const totalCf = cf * detection.qty;

    // Log if we're missing cubic feet data
    if (!detection.cubicFeet && !mapping[detection.label]?.cf) {
      console.warn(`âš ï¸ Missing cubicFeet for "${detection.label}" - using 0 as fallback`);
    }

    return sum + totalCf;
  }, 0);

  const totalWeight = detections.reduce((sum, detection) => {
    // Priority: 1) detected weight, 2) calculated from detected cubicFeet, 3) mapping table, 4) fallback to 0
    let weight = 0;
    if (detection.weight && detection.weight > 0) {
      weight = detection.weight;
    } else if (detection.cubicFeet && detection.cubicFeet > 0) {
      weight = detection.cubicFeet * 7; // Rule of thumb
    } else if (mapping[detection.label]?.cf) {
      weight = mapping[detection.label].cf * 7;
    }

    const totalWeight = weight * detection.qty;

    // Log if we're missing weight data
    if (!detection.weight && !detection.cubicFeet && !mapping[detection.label]?.cf) {
      console.warn(`âš ï¸ Missing weight for "${detection.label}" - using 0 as fallback`);
    }

    return sum + totalWeight;
  }, 0);

  // Truck capacity check: 26ft truck = ~1,700 cubic feet
  const TRUCK_CAPACITY = 1700;
  const needsMultipleTrucks = totalCubicFeet > TRUCK_CAPACITY;
  const estimatedTrucks = needsMultipleTrucks ? Math.ceil(totalCubicFeet / TRUCK_CAPACITY) : 1;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white border-b border-gray-100">
        <div className="max-w-7xl mx-auto px-6">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <button
                onClick={() => navigate('/dashboard')}
                className="text-gray-600 hover:text-gray-900"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <h1 className="text-xl font-semibold text-gray-900">Complete Your Quote</h1>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column: Form */}
          <div className="lg:col-span-2 space-y-6">
            {/* Customer Information */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">ðŸ‘¤ Customer Information</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Full Name *
                  </label>
                  <input
                    type="text"
                    value={customerInfo.name}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="John Doe"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email Address *
                  </label>
                  <input
                    type="email"
                    value={customerInfo.email}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, email: e.target.value }))}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="john@example.com"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Phone Number *
                  </label>
                  <input
                    type="tel"
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, phone: e.target.value }))}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="(555) 123-4567"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Move Date *
                  </label>
                  <input
                    type="date"
                    value={customerInfo.moveDate}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, moveDate: e.target.value }))}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    min={new Date().toISOString().split('T')[0]}
                    required
                  />
                </div>
              </div>
            </div>

            {/* Origin & Destination */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">ðŸ“ Origin & Destination</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Origin Address
                  </label>
                  <input
                    type="text"
                    value={originAddress}
                    onChange={(e) => setOriginAddress(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="123 Main St, City, State"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Origin Type
                    </label>
                    <select
                      value={originType}
                      onChange={(e) => setOriginType(e.target.value as any)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="house">House</option>
                      <option value="apartment">Apartment</option>
                      <option value="condo">Condo</option>
                      <option value="business">Business</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Origin Floor (if applicable)
                    </label>
                    <input
                      type="number"
                      min="1"
                      value={floorOrigin || ''}
                      onChange={(e) => setFloorOrigin(e.target.value ? parseInt(e.target.value) : undefined)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="e.g. 3"
                    />
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      checked={baseEstimate.stairs}
                      onChange={(e) => {/* handled by baseEstimate */}}
                      className="w-4 h-4 text-blue-600 border-gray-300 rounded"
                      disabled
                    />
                    <label className="text-sm text-gray-700">Origin has stairs</label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      checked={baseEstimate.elevator}
                      onChange={(e) => {/* handled by baseEstimate */}}
                      className="w-4 h-4 text-blue-600 border-gray-300 rounded"
                      disabled
                    />
                    <label className="text-sm text-gray-700">Origin has elevator</label>
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Origin Parking
                  </label>
                  <select
                    value={parkingOrigin}
                    onChange={(e) => setParkingOrigin(e.target.value as any)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="driveway">Driveway</option>
                    <option value="street">Street Parking</option>
                    <option value="parking_lot">Parking Lot</option>
                    <option value="difficult">Difficult/Limited</option>
                  </select>
                </div>

                <div className="border-t pt-4 mt-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Destination Address *
                    </label>
                    <input
                      type="text"
                      value={destinationAddress}
                      onChange={(e) => setDestinationAddress(e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="456 Oak Ave, City, State"
                      required
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4 mt-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Destination Type
                      </label>
                      <select
                        value={destinationType}
                        onChange={(e) => setDestinationType(e.target.value as any)}
                        className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="house">House</option>
                        <option value="apartment">Apartment</option>
                        <option value="condo">Condo</option>
                        <option value="business">Business</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Destination Floor (if applicable)
                      </label>
                      <input
                        type="number"
                        min="1"
                        value={floorDestination || ''}
                        onChange={(e) => setFloorDestination(e.target.value ? parseInt(e.target.value) : undefined)}
                        className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="e.g. 5"
                      />
                    </div>
                  </div>
                  <div className="grid grid-cols-2 gap-4 mt-4">
                    <div className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={stairsDestination}
                        onChange={(e) => setStairsDestination(e.target.checked)}
                        className="w-4 h-4 text-blue-600 border-gray-300 rounded"
                      />
                      <label className="text-sm text-gray-700">Destination has stairs</label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={elevatorDestination}
                        onChange={(e) => setElevatorDestination(e.target.checked)}
                        className="w-4 h-4 text-blue-600 border-gray-300 rounded"
                      />
                      <label className="text-sm text-gray-700">Destination has elevator</label>
                    </div>
                  </div>
                  <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Destination Parking
                    </label>
                    <select
                      value={parkingDestination}
                      onChange={(e) => setParkingDestination(e.target.value as any)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="driveway">Driveway</option>
                      <option value="street">Street Parking</option>
                      <option value="parking_lot">Parking Lot</option>
                      <option value="difficult">Difficult/Limited</option>
                    </select>
                  </div>
                </div>

                <div className="border-t pt-4 mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Distance (miles)
                    {isCalculatingDistance && (
                      <span className="ml-2 text-xs text-blue-600 flex items-center">
                        <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-blue-600 mr-1"></div>
                        Calculating...
                      </span>
                    )}
                    {isCalculatingGPT && (
                      <span className="ml-2 text-xs text-purple-600 flex items-center">
                        <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-purple-600 mr-1"></div>
                        AI Analyzing...
                      </span>
                    )}
                  </label>
                  <input
                    type="number"
                    min="0"
                    value={travelDistance}
                    onChange={(e) => setTravelDistance(parseInt(e.target.value) || 0)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Auto-calculated or enter manually"
                    disabled={isCalculatingDistance}
                  />
                  {distanceError && (
                    <p className="text-xs text-red-600 mt-1">{distanceError}</p>
                  )}
                  {gptError && (
                    <p className="text-xs text-yellow-600 mt-1">âš ï¸ {gptError} (Using fallback calculation)</p>
                  )}
                  {travelTime > 0 && !isCalculatingDistance && (
                    <p className="text-xs text-gray-600 mt-1">
                      â±ï¸ Estimated travel time: {travelTime} minutes
                    </p>
                  )}
                </div>
              </div>
            </div>

            {/* Inventory Summary */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">ðŸ“¦ Detected Inventory</h2>
              <div className="space-y-4">
                {Object.keys(groupedDetections).map(roomName => (
                  <div key={roomName} className="border-l-4 border-blue-500 pl-4">
                    <h3 className="font-medium text-gray-900 mb-2">ðŸ  {roomName}</h3>
                    <div className="space-y-2">
                      {groupedDetections[roomName].map((detection, idx) => {
                        const mappingItem = mapping[detection.label];
                        return (
                          <div key={idx} className="text-sm text-gray-600 flex items-center justify-between">
                            <span>
                              {detection.qty}x {detection.label}
                              {detection.size && <span className="text-gray-400 ml-2">({detection.size})</span>}
                            </span>
                            {mappingItem && (
                              <span className="text-gray-500">
                                {mappingItem.cf * detection.qty} cf
                              </span>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                ))}
                <div className="pt-4 border-t border-gray-200 mt-4">
                  <div className="flex justify-between text-sm">
                    <span className="font-medium text-gray-700">Total Items:</span>
                    <span className="text-gray-900">{totalItems}</span>
                  </div>
                  <div className="flex justify-between text-sm mt-1">
                    <span className="font-medium text-gray-700">Total Cubic Feet:</span>
                    <span className="text-gray-900">{totalCubicFeet.toFixed(2)} cf</span>
                  </div>
                  <div className="flex justify-between text-sm mt-1">
                    <span className="font-medium text-gray-700">Estimated Weight:</span>
                    <span className="text-gray-900">{totalWeight.toFixed(0)} lbs</span>
                  </div>

                  {/* Truck Capacity Warning */}
                  {needsMultipleTrucks && (
                    <div className="mt-4 pt-4 border-t-2 border-orange-300 bg-orange-50 rounded-lg p-3">
                      <div className="flex items-start space-x-2">
                        <svg className="w-5 h-5 text-orange-600 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                        </svg>
                        <div>
                          <div className="font-semibold text-orange-900 text-sm mb-1">
                            âš ï¸ Truck Capacity Exceeded
                          </div>
                          <p className="text-xs text-orange-800 leading-relaxed">
                            Total volume ({totalCubicFeet.toFixed(0)} cu ft) exceeds 26ft truck capacity ({TRUCK_CAPACITY} cu ft).
                            <br />
                            <strong>Required: {estimatedTrucks} truck{estimatedTrucks > 1 ? 's' : ''} or multiple trips.</strong>
                            <br />
                            This affects loading time, travel time, and overall move duration.
                          </p>
                        </div>
                      </div>
                    </div>
                  )}

                  {!needsMultipleTrucks && totalCubicFeet > 0 && (
                    <div className="mt-3 pt-3 border-t border-green-200 bg-green-50 rounded-lg p-2">
                      <div className="flex items-center space-x-2 text-xs">
                        <svg className="w-4 h-4 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                        <span className="text-green-800 font-medium">
                          âœ“ Fits in single 26ft truck ({totalCubicFeet.toFixed(0)} / {TRUCK_CAPACITY} cu ft)
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Additional Services / Upsells */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">âœ¨ Additional Services</h2>
              <div className="space-y-3">
                {upsells.map((upsell) => (
                  <label
                    key={upsell.id}
                    className={`flex items-start p-4 border-2 rounded-xl cursor-pointer transition-colors ${
                      upsell.selected
                        ? 'border-green-500 bg-green-50'
                        : upsell.recommended
                        ? 'border-yellow-300 bg-yellow-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                  >
                    <input
                      type="checkbox"
                      checked={upsell.selected}
                      onChange={() => handleUpsellToggle(upsell.id)}
                      className="mt-1 w-5 h-5 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                    />
                    <div className="ml-3 flex-1">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center space-x-2">
                            <span className="font-medium text-gray-900">{upsell.name}</span>
                            {upsell.selected && (
                              <span className="text-xs bg-green-200 text-green-800 px-2 py-0.5 rounded-full font-medium">
                                âœ“ Auto-Selected
                              </span>
                            )}
                            {upsell.recommended && !upsell.selected && (
                              <span className="text-xs bg-yellow-200 text-yellow-800 px-2 py-0.5 rounded-full">
                                Recommended
                              </span>
                            )}
                          </div>
                          <p className="text-sm text-gray-600 mt-1">{upsell.description}</p>
                        </div>
                        <span className="font-semibold text-gray-900 ml-4">
                          ${upsell.price.toFixed(2)}
                        </span>
                      </div>
                    </div>
                  </label>
                ))}
              </div>
            </div>
          </div>

          {/* Right Column: Quote Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6 sticky top-8">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">ðŸ’° Quote Summary</h2>

              <div className="space-y-4">
                {/* Truck Capacity Alert in Summary */}
                {needsMultipleTrucks && (
                  <div className="bg-orange-50 border-2 border-orange-400 rounded-xl p-3 mb-4">
                    <div className="flex items-start space-x-2">
                      <svg className="w-5 h-5 text-orange-600 mt-0.5 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                      </svg>
                      <div>
                        <div className="text-sm font-bold text-orange-900 mb-1">
                          Multiple Trucks Required
                        </div>
                        <p className="text-xs text-orange-800">
                          Volume: <strong>{totalCubicFeet.toFixed(0)} cu ft</strong> exceeds single truck capacity.
                          <br />
                          <strong>{estimatedTrucks} truck{estimatedTrucks > 1 ? 's' : ''} needed</strong> - Estimate accounts for this.
                        </p>
                      </div>
                    </div>
                  </div>
                )}

                {gptBreakdown && (
                  <div className="bg-gradient-to-br from-purple-50 to-blue-50 border border-purple-200 rounded-xl p-4 mb-4">
                    <div className="flex items-center space-x-2 mb-2">
                      <svg className="w-5 h-5 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                      </svg>
                      <span className="text-sm font-semibold text-purple-900">AI-Powered Estimate</span>
                    </div>
                    <div className="space-y-2 text-xs">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Loading:</span>
                        <span className="font-medium">{gptBreakdown.loadingHours.toFixed(1)}h</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Travel:</span>
                        <span className="font-medium">{gptBreakdown.travelHours.toFixed(1)}h</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Unloading:</span>
                        <span className="font-medium">{gptBreakdown.unloadingHours.toFixed(1)}h</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Setup:</span>
                        <span className="font-medium">{gptBreakdown.setupHours.toFixed(1)}h</span>
                      </div>
                      <div className="flex justify-between border-t pt-1">
                        <span className="text-gray-600">Buffer:</span>
                        <span className="font-medium">{gptBreakdown.bufferHours.toFixed(1)}h</span>
                      </div>
                    </div>
                  </div>
                )}

                {gptReasoning && (
                  <div className="bg-blue-50 border border-blue-200 rounded-xl p-4 mb-4">
                    <h3 className="text-xs font-semibold text-blue-900 mb-2">ðŸ¤– AI Analysis</h3>
                    <p className="text-xs text-blue-800 leading-relaxed">{gptReasoning}</p>
                  </div>
                )}

                <div>
                  <div className="flex justify-between text-sm text-gray-600 mb-1">
                    <span>Base Moving Cost:</span>
                    <span>${finalEstimate.total.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between text-sm text-gray-600 mb-1">
                    <span>Estimated Time:</span>
                    <span>{finalEstimate.hours.toFixed(1)} hours</span>
                  </div>
                  <div className="flex justify-between text-sm text-gray-600 mb-1">
                    <span>Crew Size:</span>
                    <span>{finalEstimate.crew} people</span>
                  </div>
                  <div className="flex justify-between text-sm text-gray-600">
                    <span>Travel Time:</span>
                    <span>{finalEstimate.travelMins} min</span>
                  </div>
                </div>

                {totalUpsells > 0 && (
                  <div className="pt-4 border-t border-gray-200">
                    <div className="flex justify-between text-sm text-gray-600 mb-2">
                      <span>Additional Services:</span>
                      <span>${totalUpsells.toFixed(2)}</span>
                    </div>
                  </div>
                )}

                <div className="pt-4 border-t-2 border-gray-300">
                  <div className="flex justify-between items-center">
                    <span className="text-lg font-semibold text-gray-900">Total:</span>
                    <span className="text-2xl font-bold text-blue-600">
                      ${finalTotal.toFixed(2)}
                    </span>
                  </div>
                </div>

                <div className="mt-6 space-y-3">
                  <button
                    onClick={handleSendQuote}
                    disabled={!destinationAddress || !customerInfo.name || !customerInfo.email || !customerInfo.phone || !customerInfo.moveDate}
                    className="w-full bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white font-semibold py-3 px-4 rounded-xl transition-colors disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                    <span>Send Quote via Email</span>
                  </button>

                  <button
                    onClick={handleDownloadPDF}
                    disabled={!destinationAddress || !customerInfo.name || !customerInfo.email || !customerInfo.phone || !customerInfo.moveDate}
                    className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-semibold py-3 px-4 rounded-xl transition-colors disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                    </svg>
                    <span>Download PDF Quote</span>
                  </button>

                  <button
                    onClick={handleContinue}
                    disabled={!destinationAddress || !customerInfo.name || !customerInfo.email || !customerInfo.phone || !customerInfo.moveDate}
                    className="w-full bg-gray-600 hover:bg-gray-700 disabled:bg-gray-400 text-white font-semibold py-3 px-4 rounded-xl transition-colors disabled:cursor-not-allowed"
                  >
                    Save & Continue Later
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>

    </div>
  );
}
