import React from 'react';
import { Detection, MappingTable } from '../types';

interface InventoryTableProps {
  detections: Detection[];
  mapping: MappingTable;
  onQuantityChange: (detectionIndex: number, quantity: number) => void;
  onNotesChange: (detectionIndex: number, notes: string) => void;
  isDetecting?: boolean;
}

export default function InventoryTable({
  detections,
  mapping,
  onQuantityChange,
  onNotesChange,
  isDetecting = false
}: InventoryTableProps) {
  // Group detections by room and collect photo URLs
  const groupedDetections = detections.reduce((groups, detection) => {
    const room = detection.room || 'Unknown Room';
    if (!groups[room]) {
      groups[room] = [];
    }
    groups[room].push(detection);
    return groups;
  }, {} as Record<string, Detection[]>);

  const roomNames = Object.keys(groupedDetections).sort();

  // Collect unique photo URLs for each room
  const roomPhotos = roomNames.reduce((photos, room) => {
    const photoUrls = groupedDetections[room]
      .map(detection => detection.sourcePhotoId)
      .filter(Boolean) as string[];

    const uniquePhotos = Array.from(new Set(photoUrls));
    photos[room] = uniquePhotos;
    return photos;
  }, {} as Record<string, string[]>);

  // Calculate totals
  const totalItems = detections.reduce((sum, detection) => sum + detection.qty, 0);
  const totalCubicFeet = detections.reduce((sum, detection) => {
    const mappingItem = mapping[detection.label];
    return sum + (mappingItem ? mappingItem.cf * detection.qty : 0);
  }, 0);

  if (detections.length === 0) {
    return (
      <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-8">
        <div className="text-center py-8">
          {isDetecting ? (
            <>
              <div className="mx-auto w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mb-3">
                <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">AI Analyzing Photos...</h3>
              <p className="text-sm text-blue-600 mb-4">Detecting furniture in real-time</p>
              <div className="bg-blue-50 rounded-lg p-4">
                <p className="text-sm text-blue-800">
                  üéØ <strong>Real-time Detection:</strong> Watch as furniture appears in the inventory as each photo is analyzed!
                </p>
              </div>
            </>
          ) : (
            <>
              <div className="mx-auto w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mb-3">
                <svg className="w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
              </div>
              <h3 className="text-sm font-medium text-gray-900 mb-1">No detections yet</h3>
              <p className="text-xs text-gray-500">AI will detect furniture automatically</p>
            </>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl shadow-sm border border-gray-100">
      <div className="p-8 border-b border-gray-100">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-lg font-semibold text-gray-900 mb-2">Detected Inventory</h2>
            <p className="text-sm text-gray-600">
              {isDetecting ? 'AI is analyzing photos and detecting furniture...' : 'Furniture and items found by AI analysis'}
            </p>
          </div>
          {isDetecting && (
            <div className="flex items-center text-sm text-blue-600 bg-blue-50 px-3 py-2 rounded-lg">
              <div className="animate-spin rounded-full h-4 w-4 border-2 border-blue-600 border-t-transparent mr-2"></div>
              <span>Live Detection</span>
            </div>
          )}
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Item
              </th>
              <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Quantity
              </th>
              <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Notes
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-100">
            {roomNames.map((roomName) => (
              <React.Fragment key={roomName}>
                {/* Room Header */}
                <tr className="bg-blue-50">
                  <td colSpan={3} className="px-6 py-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <span className="text-sm font-semibold text-blue-800">
                          üè† {roomName}
                        </span>
                        {roomPhotos[roomName] && roomPhotos[roomName].length > 0 && (
                          <div className="flex items-center space-x-1">
                            <span className="text-xs text-blue-600">from:</span>
                            <div className="flex space-x-1">
                              {roomPhotos[roomName].slice(0, 3).map((photoUrl, idx) => (
                                <div
                                  key={idx}
                                  className="w-6 h-6 rounded border border-blue-200 overflow-hidden bg-gray-100"
                                >
                                  <img
                                    src={photoUrl}
                                    alt={`Photo ${idx + 1}`}
                                    className="w-full h-full object-cover"
                                    onError={(e) => {
                                      e.currentTarget.style.display = 'none';
                                    }}
                                  />
                                </div>
                              ))}
                              {roomPhotos[roomName].length > 3 && (
                                <div className="w-6 h-6 rounded border border-blue-200 bg-blue-100 flex items-center justify-center">
                                  <span className="text-xs text-blue-600 font-medium">
                                    +{roomPhotos[roomName].length - 3}
                                  </span>
                                </div>
                              )}
                            </div>
                          </div>
                        )}
                      </div>
                      <div className="text-xs text-blue-600">
                        {groupedDetections[roomName].length} item{groupedDetections[roomName].length !== 1 ? 's' : ''}
                      </div>
                    </div>
                  </td>
                </tr>
                {/* Room Items */}
                {groupedDetections[roomName].map((detection, index) => {
                  const mappingItem = mapping[detection.label];
                  const originalQty = detection.qty; // This would come from AI detection

                  return (
                    <tr key={`${roomName}-${index}`} className="hover:bg-gray-50">
                      <td className="px-6 py-4 text-sm text-gray-900">
                        <div className="font-medium">{detection.label}</div>
                        {mappingItem && (
                          <div className="text-xs text-gray-500 mt-1">
                            {mappingItem.cf} cf ‚Ä¢ {mappingItem.minutes} min
                            {mappingItem.wrap && ' ‚Ä¢ Wrap needed'}
                          </div>
                        )}
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900">
                        <div className="flex items-center gap-1">
                          <button
                            onClick={() => onQuantityChange(index, Math.max(0, detection.qty - 1))}
                            className="w-7 h-7 bg-gray-100 hover:bg-gray-200 rounded-md flex items-center justify-center transition-colors"
                          >
                            <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
                            </svg>
                          </button>
                          <div className="w-12 text-center font-medium text-gray-900 bg-gray-50 rounded-md py-1">
                            {detection.qty}
                          </div>
                          <button
                            onClick={() => onQuantityChange(index, detection.qty + 1)}
                            className="w-7 h-7 bg-gray-100 hover:bg-gray-200 rounded-md flex items-center justify-center transition-colors"
                          >
                            <svg className="w-4 h-4 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                            </svg>
                          </button>
                          {detection.qty !== originalQty && (
                            <span className="text-xs text-blue-600 ml-2">
                              AI: {originalQty}
                            </span>
                          )}
                        </div>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900">
                        <div className="relative">
                          <textarea
                            value={detection.notes || ''}
                            onChange={(e) => onNotesChange(index, e.target.value)}
                            placeholder="Add notes about this item..."
                            rows={2}
                            className="w-full border border-gray-200 rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none bg-gray-50"
                          />
                          {detection.notes && (
                            <div className="absolute -top-1 -right-1 w-2 h-2 bg-blue-500 rounded-full"></div>
                          )}
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>

      {/* Totals */}
      <div className="p-8 bg-gray-50 border-t border-gray-100 flex justify-between items-center">
        <div className="text-sm font-medium text-gray-700">
          Total Items: <span className="font-semibold text-gray-900">{totalItems}</span>
        </div>
        <div className="text-sm font-medium text-gray-700">
          Total Cubic Feet: <span className="font-semibold text-gray-900">{totalCubicFeet.toFixed(2)} cf</span>
        </div>
      </div>
    </div>
  );
}
