import React, { useState, useEffect } from 'react';
import SearchPanel from './components/SearchPanel';
import PhotoGallery from './components/PhotoGallery';
import InventoryTable from './components/InventoryTable';
import EstimatePanel from './components/EstimatePanel';
import { AppState, Photo, Detection, MappingTable, Estimate, QuotePayload } from './types';
import { calculateEstimate } from './lib/estimate';
import { toCSV, generatePdf, downloadFile } from './lib/export';

// Mock data for demonstration
const mockPhotos: Photo[] = [
  {
    id: '1',
    url: 'https://via.placeholder.com/400x300',
    thumbnailUrl: 'https://via.placeholder.com/150x100',
    filename: 'living-room.jpg',
    uploadedAt: new Date()
  },
  {
    id: '2',
    url: 'https://via.placeholder.com/400x300',
    thumbnailUrl: 'https://via.placeholder.com/150x100',
    filename: 'bedroom.jpg',
    uploadedAt: new Date()
  },
  {
    id: '3',
    url: 'https://via.placeholder.com/400x300',
    thumbnailUrl: 'https://via.placeholder.com/150x100',
    filename: 'kitchen.jpg',
    uploadedAt: new Date()
  }
];

const mockDetections: Detection[] = [
  {
    label: 'Sofa',
    qty: 1,
    confidence: 0.95,
    sourcePhotoId: '1',
    notes: ''
  },
  {
    label: 'Dining Table',
    qty: 1,
    confidence: 0.87,
    sourcePhotoId: '2',
    notes: ''
  },
  {
    label: 'Refrigerator',
    qty: 1,
    confidence: 0.92,
    sourcePhotoId: '3',
    notes: ''
  }
];

const mockMapping: MappingTable = {
  'Sofa': { cf: 25, minutes: 30, wrap: true },
  'Dining Table': { cf: 15, minutes: 20, wrap: true },
  'Refrigerator': { cf: 35, minutes: 45, wrap: false },
  'Chair': { cf: 3, minutes: 5, wrap: true },
  'Bed': { cf: 20, minutes: 25, wrap: true },
  'Dresser': { cf: 12, minutes: 15, wrap: true },
  'TV': { cf: 8, minutes: 10, wrap: true },
  'Bookshelf': { cf: 10, minutes: 12, wrap: false }
};

function App() {
  const [state, setState] = useState<AppState>({
    address: '',
    photos: [],
    detections: [],
    mapping: mockMapping,
    estimate: {
      crew: 3,
      rate: 75,
      travelMins: 30,
      stairs: false,
      elevator: false,
      wrapping: false,
      safetyPct: 10,
      hours: 0,
      total: 0
    }
  });

  const [selectedPhotos, setSelectedPhotos] = useState<string[]>([]);
  const [recentSearches, setRecentSearches] = useState<string[]>([]);
  const [toasts, setToasts] = useState<Array<{ id: string; message: string; type: 'success' | 'error' }>>([]);

  // Update estimate when detections or estimate params change
  useEffect(() => {
    if (state.detections.length > 0) {
      const result = calculateEstimate(state.detections, state.mapping, {
        crew: state.estimate.crew,
        rate: state.estimate.rate,
        travelMins: state.estimate.travelMins,
        stairs: state.estimate.stairs,
        elevator: state.estimate.elevator,
        wrapping: state.estimate.wrapping,
        safetyPct: state.estimate.safetyPct
      });

      setState(prev => ({
        ...prev,
        estimate: {
          ...prev.estimate,
          hours: result.hours,
          total: result.total
        }
      }));
    }
  }, [state.detections, state.estimate.crew, state.estimate.rate, state.estimate.travelMins, state.estimate.stairs, state.estimate.elevator, state.estimate.wrapping, state.estimate.safetyPct]);

  const addToast = (message: string, type: 'success' | 'error') => {
    const id = Date.now().toString();
    setToasts(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setToasts(prev => prev.filter(toast => toast.id !== id));
    }, 3000);
  };

  const handleAddressChange = (address: string) => {
    setState(prev => ({ ...prev, address }));
  };

  const handleFetchPhotos = () => {
    if (!state.address.trim()) {
      addToast('Please enter an address first', 'error');
      return;
    }

    setState(prev => ({ ...prev, photos: mockPhotos }));
    addToast('Photos fetched successfully', 'success');
  };

  const handleClear = () => {
    setState(prev => ({
      ...prev,
      address: '',
      photos: [],
      detections: []
    }));
    setSelectedPhotos([]);
    addToast('Cleared all data', 'success');
  };

  const handlePhotoSelect = (photoId: string) => {
    setSelectedPhotos(prev => 
      prev.includes(photoId) 
        ? prev.filter(id => id !== photoId)
        : [...prev, photoId]
    );
  };

  const handleRunDetection = () => {
    if (selectedPhotos.length === 0) {
      addToast('Please select photos first', 'error');
      return;
    }

    setState(prev => ({ ...prev, detections: mockDetections }));
    addToast('AI detection completed', 'success');
  };

  const handleQuantityChange = (detectionIndex: number, quantity: number) => {
    setState(prev => ({
      ...prev,
      detections: prev.detections.map((detection, index) =>
        index === detectionIndex ? { ...detection, qty: quantity } : detection
      )
    }));
  };

  const handleNotesChange = (detectionIndex: number, notes: string) => {
    setState(prev => ({
      ...prev,
      detections: prev.detections.map((detection, index) =>
        index === detectionIndex ? { ...detection, notes } : detection
      )
    }));
  };

  const handleEstimateChange = (field: keyof Estimate, value: any) => {
    setState(prev => ({
      ...prev,
      estimate: { ...prev.estimate, [field]: value }
    }));
  };

  const handleSendSMS = () => {
    addToast('SMS quote sent successfully', 'success');
  };

  const handleSendEmail = () => {
    addToast('Email quote sent successfully', 'success');
  };

  const handleDownloadPDF = async () => {
    const quotePayload: QuotePayload = {
      address: state.address,
      detections: state.detections,
      estimate: state.estimate,
      timestamp: new Date()
    };

    const pdfBlob = await generatePdf(quotePayload);
    downloadFile(pdfBlob, 'quote.pdf', 'application/pdf');
    addToast('PDF downloaded successfully', 'success');
  };

  const handleCopyCSV = () => {
    const csvContent = toCSV(state.detections);
    navigator.clipboard.writeText(csvContent);
    addToast('CSV copied to clipboard', 'success');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-gray-900">Instant Quote Builder</h1>
            </div>
            <div className="flex items-center space-x-4">
              <select className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500">
                <option>San Francisco, CA</option>
                <option>Los Angeles, CA</option>
                <option>New York, NY</option>
                <option>Chicago, IL</option>
              </select>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Column 1: Search */}
          <div className="space-y-6">
            <SearchPanel
              address={state.address}
              onAddressChange={handleAddressChange}
              onFetchPhotos={handleFetchPhotos}
              onClear={handleClear}
              recentSearches={recentSearches}
            />
          </div>

          {/* Column 2: Photos & Inventory */}
          <div className="space-y-6">
            <PhotoGallery
              photos={state.photos}
              selectedPhotos={selectedPhotos}
              onPhotoSelect={handlePhotoSelect}
              onRunDetection={handleRunDetection}
            />
            <InventoryTable
              detections={state.detections}
              mapping={state.mapping}
              onQuantityChange={handleQuantityChange}
              onNotesChange={handleNotesChange}
            />
          </div>

          {/* Column 3: Estimate */}
          <div className="space-y-6">
            <EstimatePanel
              estimate={state.estimate}
              onEstimateChange={handleEstimateChange}
              onSendSMS={handleSendSMS}
              onSendEmail={handleSendEmail}
              onDownloadPDF={handleDownloadPDF}
              onCopyCSV={handleCopyCSV}
            />
          </div>
        </div>
      </main>

      {/* Toast Notifications */}
      <div className="fixed top-4 right-4 space-y-2 z-50">
        {toasts.map(toast => (
          <div
            key={toast.id}
            className={`px-4 py-2 rounded-md shadow-lg text-sm font-medium ${
              toast.type === 'success' 
                ? 'bg-green-100 text-green-800 border border-green-200' 
                : 'bg-red-100 text-red-800 border border-red-200'
            }`}
          >
            {toast.message}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
