import React, { useState } from 'react';
import { Photo } from '../types';

interface PhotoGalleryProps {
  photos: Photo[];
  selectedPhotos: string[];
  onPhotoSelect: (photoId: string) => void;
  onRunDetection: () => void;
  isDetecting?: boolean;
  onSelectAll?: () => void;
  onDeselectAll?: () => void;
}

export default function PhotoGallery({
  photos,
  selectedPhotos,
  onPhotoSelect,
  onRunDetection,
  isDetecting = false,
  onSelectAll,
  onDeselectAll
}: PhotoGalleryProps) {
  const handleSelectAll = () => {
    if (selectedPhotos.length === photos.length) {
      // If all are selected, deselect all
      if (onDeselectAll) {
        onDeselectAll();
      } else {
        // Fallback: deselect each photo individually
        selectedPhotos.forEach(photoId => onPhotoSelect(photoId));
      }
    } else {
      // Select all photos
      if (onSelectAll) {
        onSelectAll();
      } else {
        // Fallback: select each photo individually
        photos.forEach(photo => {
          if (!selectedPhotos.includes(photo.id)) {
            onPhotoSelect(photo.id);
          }
        });
      }
    }
  };

  if (photos.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Photos & Inventory</h2>

        <div className="text-center py-12">
          <div className="mx-auto w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mb-4">
            <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No photos yet</h3>
          <p className="text-gray-500 mb-4">Upload photos to get started with AI detection</p>
          <div className="text-sm text-gray-400 space-y-1">
            <p>• Take clear photos of items</p>
            <p>• Include multiple angles</p>
            <p>• Ensure good lighting</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-lg font-semibold text-gray-900">Photos & Inventory</h2>
        <div className="flex items-center gap-2">
          <button
            onClick={() => setViewMode('grid')}
            className={`p-2 rounded ${viewMode === 'grid' ? 'bg-blue-100 text-blue-600' : 'text-gray-400 hover:text-gray-600'}`}
          >
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
              <path d="M5 3a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2V5a2 2 0 00-2-2H5zM5 11a2 2 0 00-2 2v2a2 2 0 002 2h2a2 2 0 002-2v-2a2 2 0 00-2-2H5zM11 5a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2V5zM11 13a2 2 0 012-2h2a2 2 0 012 2v2a2 2 0 01-2 2h-2a2 2 0 01-2-2v-2z" />
            </svg>
          </button>
          <button
            onClick={() => setViewMode('list')}
            className={`p-2 rounded ${viewMode === 'list' ? 'bg-blue-100 text-blue-600' : 'text-gray-400 hover:text-gray-600'}`}
          >
            <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M3 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm0 4a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />
            </svg>
          </button>
        </div>
      </div>

      {/* Selection Controls */}
      <div className="flex items-center justify-between mb-4 p-3 bg-gray-50 rounded-lg">
        <div className="flex items-center gap-3">
          <button
            onClick={handleSelectAll}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              selectedPhotos.length === photos.length
                ? 'bg-red-600 text-white hover:bg-red-700'
                : 'bg-green-600 text-white hover:bg-green-700'
            }`}
          >
            {selectedPhotos.length === photos.length ? 'Deselect All' : 'Select All'}
          </button>
          <span className="text-sm text-gray-600">
            {selectedPhotos.length} of {photos.length} selected
          </span>
        </div>
        {selectedPhotos.length > 0 && (
          <div className="text-sm text-blue-600 font-medium">
            Ready for AI detection
          </div>
        )}
      </div>

      {/* Photo Gallery */}
      <div className={`mb-4 ${viewMode === 'grid' ? 'grid grid-cols-2 gap-2' : 'space-y-2'}`}>
        {photos.map((photo) => (
          <div
            key={photo.id}
            className={`relative cursor-pointer rounded-lg overflow-hidden border-2 transition-all ${
              selectedPhotos.includes(photo.id)
                ? 'border-blue-500 ring-2 ring-blue-200'
                : 'border-gray-200 hover:border-gray-300'
            }`}
            onClick={() => onPhotoSelect(photo.id)}
          >
            <img
              src={photo.thumbnailUrl}
              alt={photo.filename}
              className={`w-full ${viewMode === 'grid' ? 'h-24 object-cover' : 'h-16 object-cover'}`}
            />

            {/* Selection indicator */}
            {selectedPhotos.includes(photo.id) && (
              <div className="absolute top-1 right-1 w-5 h-5 bg-blue-500 rounded-full flex items-center justify-center">
                <svg className="w-3 h-3 text-white" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                </svg>
              </div>
            )}

            {/* Photo info overlay */}
            <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-50 text-white text-xs p-1">
              <div className="truncate">{photo.filename}</div>
            </div>
          </div>
        ))}
      </div>

      {/* Run Detection Button */}
      <button
        onClick={onRunDetection}
        disabled={selectedPhotos.length === 0 || isDetecting}
        className="w-full bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
      >
        {isDetecting ? (
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
            AI Detecting Furniture...
          </div>
        ) : (
          `Run Detection (${selectedPhotos.length} photos selected)`
        )}
      </button>
    </div>
  );
}
