/**
 * React-PDF Service
 * Generate beautiful PDF quotes using React components
 */

import { pdf } from '@react-pdf/renderer';
import React from 'react';
import { QuotePDF } from '../components/QuotePDF';

interface QuoteData {
  id: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  moveDate: string;
  originAddress: string;
  destinationAddress: string;
  detections: any[];
  estimate: any;
  upsells: any[];
  totalAmount: number;
  photos?: any[];
}

export class ReactPDFService {
  static async downloadQuote(quoteData: QuoteData): Promise<void> {
    try {
      // Create the PDF document
      const doc = pdf(React.createElement(QuotePDF, { quoteData }));
      const blob = await doc.toBlob();

      // Create download link
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `quote-${quoteData.id}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error generating PDF:', error);
      throw new Error('Failed to generate PDF');
    }
  }

  static async getQuoteBlob(quoteData: QuoteData): Promise<Blob> {
    try {
      const doc = pdf(React.createElement(QuotePDF, { quoteData }));
      return await doc.toBlob();
    } catch (error) {
      console.error('Error generating PDF blob:', error);
      throw new Error('Failed to generate PDF blob');
    }
  }
}
